package corejavapart1;

public class ConcatOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="yenugudhati  ";
	    String s2="  tanuja";
	    String s3=s1.concat(s2);
	    String s4=s1+s2;
	    System.out.println("After concate using method ---->"+s3);
	    System.out.println("After concate using +   ---->"+s4);

	}

}
